* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  font-family: "Overpass", sans-serif;
  color: white;
  text-align: center;
}

h1,
h2,
h3,
h4 {
  font-family: "Overpass", sans-serif;
}

img {
  max-width: 100%;
}

ul {
  list-style: none;
}

a {
  text-decoration: none;
  color: white;
}

.container {
  width: 85%;
  max-width: 400px;
  margin: 0 auto;
}

.btn {
  display: inline-block;
  color: white;
  padding: 0.8em 1.6em;
  border-radius: 100px;
  font-family: "Ubuntu", sans-serif;
  font-weight: 700;
  transition: all 0.2s ease-in-out;
}
.btn:hover, .btn:focus {
  opacity: 0.8;
  transform: scale(0.95);
}

.btn-light {
  background: white;
  color: #ff3d54;
}
.btn-light:hover, .btn-light:focus {
  background: #ff7a85;
  color: white;
}

.btn-transparent {
  background: transparent;
  border: 1px solid white;
}

.btn-gradient {
  background: linear-gradient(to bottom right, #ff8f70, #ff3d54);
  color: white;
}

.desktop {
  display: none;
}

.block {
  display: block;
}

/****************
        Nav
*****************/
.logo {
  width: 25%;
}

nav {
  overflow: hidden;
  width: 85%;
  margin: 0 auto;
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 3.5em 0 3.1em 0;
}
nav hr {
  color: #ffffff3b;
}

.nav {
  position: absolute;
  left: 2.5rem;
  top: 5rem;
  background: white;
  border-radius: 5px;
  width: 80%;
  text-align: center;
  transition: transform 0.3s ease-in-out;
  transform: translateX(150%);
}
.nav.active {
  transform: translateX(0);
}
.nav .nav-item {
  margin: 1em 0 0.5em 0;
}
.nav .nav-link {
  color: #25252d;
  display: inline-block;
  padding: 0.5em;
}

.nav-primary .menu-main {
  position: relative;
}
.nav-primary .menu-main:hover .menu-sub {
  display: block;
  margin: 0.5em 0;
}
.nav-primary .menu-sub {
  display: none;
  position: relative;
  left: 2rem;
  background: #f3f3f3;
  border-radius: 3px;
  width: 80%;
  padding-bottom: 0.5em;
}
.nav-primary .sub-link {
  display: inline-block;
  width: 100%;
  color: #1f3f5b;
  margin: 1em 0 0.5em 0;
}
.nav-primary .sub-link:hover {
  color: #c8c8cb;
}

.nav-secondary {
  padding-bottom: 1em;
}

.nav-toggle {
  cursor: pointer;
  width: 31px;
}
.nav-toggle .close-hamburger {
  display: none;
}
.nav-toggle.active .hamburger {
  display: none;
}
.nav-toggle.active .close-hamburger {
  display: block;
}

/****************
        Hero
*****************/
header {
  position: relative;
  background: url("/8b5970dece860c2507f61c526f5cc02a.svg") no-repeat -20rem -15rem/cover, linear-gradient(to bottom right, #ff8f70, #ff3d54) no-repeat center center/cover;
  border-bottom-left-radius: 100px;
  background-size: 350%, 100%;
}

.hero {
  padding: 3.6em 0 9.5em 0;
  text-align: center;
}
.hero .hero-text h1 {
  font-size: 2.1rem;
}
.hero .hero-text p {
  padding: 1em 2em 1.8em 1em;
  margin-bottom: 1.5rem;
}
.hero .hero-links {
  display: flex;
  justify-content: space-between;
  width: 95%;
  margin: 0 auto;
}

/****************
        Intro
        *****************/
.intro {
  color: black;
}
.intro .intro-title {
  color: #1f3f5b;
  padding: 5.8em 0 3em 0;
  font-size: 1.1rem;
}
.intro .img-col .mobile {
  transform: scale(1.1);
}
.intro .text-col h3 {
  font-size: 1.7rem;
  color: #1f3f5b;
  margin-top: 2.2em;
  line-height: 1.2;
}
.intro .text-col p {
  font-size: 1.1rem;
  line-height: 1.5;
  color: #4b5862;
  font-weight: 300;
  margin: 1em 0 4.5em 0;
}
.intro .intro-mid {
  position: relative;
  margin-bottom: 5rem;
}
.intro .intro-mid .text-col {
  position: absolute;
  z-index: -1;
  height: 38rem;
  top: 46%;
  background: url(/d6cc1120f6f7eb80ffd28ae37ab6f122.svg) no-repeat, linear-gradient(#2c2d3f, #3f4164) no-repeat;
  display: flex;
  background-size: 165%, 100%;
  background-position: center 1900%, center center;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  border-radius: 0 100px 0 100px;
}
.intro .intro-mid .text-col h3,
.intro .intro-mid .text-col p {
  color: white;
  width: 80%;
}
.intro .intro-mid .text-col h3 {
  font-size: 2.4rem;
  margin-top: 4.5em;
  line-height: 1.2;
}
.intro .intro-mid .text-col p {
  font-size: 1rem;
  color: #c8c8cb;
}
.intro .intro-bottom {
  margin-top: 31rem;
}
.intro .intro-bottom .img-col .mobile {
  transform: scale(1.3);
}

/****************
        Footer
*****************/
footer {
  background: #25252d;
  border-radius: 0 100px 0 0;
  font-family: "Ubuntu", sans-serif;
}
footer .footer-logo {
  display: inline-block;
  margin: 4.7rem 0;
}
footer .footer-menu h4 {
  font-family: "Ubuntu", sans-serif;
  margin-bottom: 2em;
}
footer .footer-menu h4 + h4 {
  margin-top: 3em;
}
footer .footer-menu ul li:last-child {
  padding-bottom: 3rem;
}
footer .footer-menu a {
  display: inline-block;
  margin-bottom: 1em;
  color: #c8c8cb;
}
footer .footer-menu a:hover, footer .footer-menu afocus {
  text-decoration: underline;
}

