// font-family: 'Overpass', sans-serif;
// font-family: 'Ubuntu', sans-serif;
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  // background: $body-gradient;
  // font-family: "Ubuntu", sans-serif;
  font-family: "Overpass", sans-serif;
  color: $white;
  text-align: center;
  // line-height: 1.5;
}
h1,
h2,
h3,
h4 {
  font-family: "Overpass", sans-serif;
  // letter-spacing: 1px;
}
img {
  max-width: 100%;
}

ul {
  list-style: none;
}
a {
  text-decoration: none;
  color: $white;
}

.container {
  width: 85%;
  max-width: 400px;
  margin: 0 auto;
}

.btn {
  display: inline-block;
  color: white;
  padding: 0.8em 1.6em;
  border-radius: 100px;
  font-family: "Ubuntu", sans-serif;
  font-weight: 700;
  transition: all 0.2s ease-in-out;
  &:hover,
  &:focus {
    opacity: 0.8;
    transform: scale(0.95);
  }
}

.btn-light {
  background: white;
  color: $light-red-2;
  &:hover,
  &:focus {
    background: $very-light-red-1;
    color: white;
  }
}
.btn-transparent {
  background: transparent;
  border: 1px solid white;
}
.btn-gradient {
  background: linear-gradient(to bottom right, $very-light-red-2, $light-red-2);
  color: white;
}

