// Primary
$light-red-1: hsl(356, 100%, 66%); //(CTA text)
$very-light-red-1: hsl(355, 100%, 74%); //(CTA hover background)
$very-dark-blue: hsl(208, 49%, 24%); //(headings)

// Neutral
$white: hsl(0, 0%, 100%); //(text)
$grayish-blue: hsl(240, 2%, 79%); //(footer text)
$very-dark-grayish-blue: hsl(207, 13%, 34%); //(body copy)
$very-dark-black-blue: hsl(240, 10%, 16%); // (footer background)

//Gradient
$very-light-red-2: hsl(13, 100%, 72%);
$light-red-2: hsl(353, 100%, 62%);

$background-gradient: linear-gradient(
  $very-light-red-2,
  $light-red-2
); //Intro/CTA mobile nav

$very-dark-gray-blue: hsl(237, 17%, 21%);
$very-dark-desaturated-blue: hsl(237, 23%, 32%);

$body-gradient: linear-gradient(
  $very-dark-gray-blue,
  $very-dark-desaturated-blue
);

