  .intro {
    .intro-title {
      font-size: 1.6rem;
      padding: 0;
      padding-top: 5.9em;
      word-spacing: -3px;
    }
    .intro-top,
    .intro-mid,
    .intro-bottom {
      text-align: left;
      .img-col {
        width: 40%;
      }
      .text-col {
        width: 37%;
      }
    }

    //Top
    .intro-top {
      .row {
        flex-direction: row-reverse;
      }
      .img-col {
        transform: translate(17%, -2%);
        img {
          transform: scale(1.6);
        }
      }
      .text-col {
        margin-top: 6.8em;
        h3 {
          margin: 0;
          padding: 0;
        }
        p {
          line-height: 1.6;
          margin-top: 1.5em;
          padding: 0;
        }
      }
    }

    //Mid
    .intro-mid {
      position: relative;
      margin-top: 10.5rem;
      height: 25rem;
      background: url(../images/bg-pattern-circles.svg) no-repeat,
        $body-gradient no-repeat;
      background-size: 70%, 100%;
      background-position: -48% 81%, center center;
      border-radius: 0 100px 0 100px;

      .text-col {
        margin-bottom: 13.2em;
        h3,
        p {
          color: white;
          width: 100%;
        }
        h3 {
          font-size: 2.4rem;
          margin: 0;
          line-height: 1.2;
        }
        p {
          margin: 0;
          margin-top: 1.5em;
          font-size: 1rem;
          color: $grayish-blue;
        }

        position: initial;
        z-index: initial;
        height: 100%;
        background: none;
      }
      .img-col {
        transform: translate(8rem, -4.3rem);
      }
    }

    //Bottom
    .intro-bottom {
      margin-top: 19rem;
      padding-bottom: 7rem;
      .text-col {
        h3 {
          margin: 0;
        }
        p {
          margin: 1.6em 0 4.5em 0;
        }
      }
      .img-col {
        transform: translate(-14%, -8.5%);
        img {
          transform: scale(1.7);
        }
      }
    }
  }

  //Footer

  footer {
    background: $very-dark-black-blue;
    border-radius: 0 100px 0 0;
    font-family: "Ubuntu", sans-serif;
    padding-top: 4.3rem;
    margin-top: 7.5rem;
    .row {
      align-items: flex-start;
      justify-content: center;
      gap: 8rem;
      margin-right: 20rem;
      .footer-logo {
        width: 17%;
      }
      .footer-menu {
        width: 55%;
        text-align: left;
      }
    }
    .footer-logo {
      margin: 0;
    }

    .footer-menu {
      display: flex;
      gap: 13rem;
    }
  }
}
